import React, { useEffect } from 'react'
import Aos from "aos"
import "aos/dist/aos.css"

function Projects() {

  useEffect(() => {
    Aos.init({ duration: 1500 });
},[])


let Letters = function() {
  this.lettersDOM = null;
  this.active = null;
  letters = [];
  this.alphabet = ["a", "b", "c", "d","e","f","g","h","i","j","k","l","m","n","o","p","q","r","s","t","i","u","v","w","x","y","z","~","&","|","^","ç","@","]","[","{","}","ù","*","µ","¤","$","£","€","°",")","(","+","-","/","<",">","²","`","é","è","1","2","3","4","5","6","7","8","9","0"
  ];

  return this;
};

Letters.prototype.init = function( word ) {
  
  this.lettersDOM = document.querySelectorAll('.letter');
  this.active = true;
  var i;
  var nextChar;
  var lettersMax = this.lettersDOM.length;
  
  for ( i = 0; i < this.lettersDOM.length; i++ ) {
    
    if ( word.charAt( i ) != "" )
      nextChar = word.charAt( i );
    else 
      nextChar = false;
    
    this.letters.push( new Letter( this.lettersDOM[ i ],  nextChar ) );
    
  }
  
  if ( word.length > lettersMax ) {
    
    var wordContainer = document.getElementById("word");

    for ( i = lettersMax; i < word.length; i++ ) {
      var letterSpan = document.createElement('span');
      letterSpan.innerHTML = "";
      letterSpan.classList.add('letter');
      wordContainer.appendChild( letterSpan );
      this.letters.push( new Letter( letterSpan,  word.charAt( i ) ) );
    }
  }
  
  this.animate();
  
  return this;
  
};

Letters.prototype.animate = function() {
  var i;
  var random;
  var char;
  
  if ( this.active ) {
 
    window.requestAnimationFrame( this.animate.bind(this) );
    
    var indexes = [];

    for ( i = 0; i < this.letters.length; i++ ) {
    
      var current = this.letters[ i ];  
      
      if ( !current.isDead ) {     
        random = Math.floor(Math.random() * (this.alphabet.length - 0));
        char = this.alphabet[ random ]; 
        current.render( char );
      } else {
        indexes.push( i );
      }
    } 
    
    for ( i = 0; i < indexes.length; i++ ) {
      this.letters.splice( indexes[ i ], 1 );
    }
    
    if ( this.letters.length == 0 ) {
      this.stop();
    }
  }
};

Letters.prototype.start = function( word ) {
  this.init( word );
};

Letters.prototype.stop = function() {
  this.active = false;
};

let Letter = function( DOMElement, nextChar ) {
  
  var scope = this;
  
  this.DOMEl = DOMElement;
  this.char = DOMElement.innerHTML;
  this.next = nextChar;
  this.speed = Math.floor(Math.random() * (300 - 50) );
  this.total = 0;
  this.duration = 2000;
  this.animating = true;
  this.isDead = false;
  
  this.timer = setInterval(function() { 
    if ( scope.animating === true ) {
      scope.total += scope.speed;
    } 
    scope.animating = !scope.animating;
  }, this.speed);

  this.animate();
  
  return this;
 
};

Letter.prototype.animate = function() {
  var i;
  var random;
  
  if ( !this.isDead ) {
    window.requestAnimationFrame( this.animate.bind(this) );
  }
  
  
  if ( this.total < this.duration ) {
    
    if ( this.animating ) {
      this.DOMEl.innerHTML = this.char;
    }
      
  } else {
    this.isDead = true;
    
    if ( !this.next ) {
      var parent = document.getElementById('word');
      parent.removeChild( this.DOMEl );
      return;
    }
    
    this.DOMEl.innerHTML = this.next;
  }
};

Letter.prototype.render = function( char ) {
  
  if ( !this.animating ) {
    this.char = char;
  }
  
};

var word = [ "CODEDOODL.ES", "ANIMATION", "INSPIRATION" ];
var nextWord = 1;

var letters = new Letters();

setTimeout( function() {
  
  letters.start( word[ nextWord ] );
  
  setInterval(function() {
    nextWord++;
    if ( nextWord >= word.length )
      nextWord = 0;
    
    letters.start( word[ nextWord ] );
  }, 10000);
  
}, 2000);



return(
  <>
  
  <div class="projects" id="projects">
    <div clssName='title-container'>

    <div class="center">
  <h1 id="word">
    <span class="letter">C</span>
    <span class="letter">O</span>
    <span class="letter">D</span>
    <span class="letter">E</span>
    <span class="letter">D</span>
    <span class="letter">O</span>
    <span class="letter">O</span>
    <span class="letter">D</span>
    <span class="letter">L</span>
    <span class="letter">.</span>
    <span class="letter">E</span>
    <span class="letter">S</span>
  </h1>
  <div id="test"></div>
</div>

  <h1 data-aos="fade-right" className="project-title">Projects</h1>
  </div>

  <div data-aos="fade-right" className="project-right HRF-right">
    <div className ='p-left-content'>
      <h1 className="left-title nasa-title">Human Rights First</h1>
       <p className='languages-used'> JavaScript | React | Less | Ant Design | Node.js | PostgreSQL | Figma 
       </p>
       <p className='project-about-p'>I collaborated remotely with a team of Web Developers, UX/UI Lead Designers and Data Scientists to add new features.</p>
      <div className='left-buttons'>
      <a className='left-links' href="https://github.com/CatRadin/human-rights-first-asylum-fe-a" 
        target="_blank">Github</a> 
        <a className='left-links' href="https://a.humanrightsfirstasylum.dev/login" 
        target="_blank">Site</a>
      </div>
    </div>
    <div className='p-left-image'>
    <img className="project-img HRF" src="https://i.ibb.co/m0hW3ZT/Human-Rights-First-Asylum.png"></img>
    </div>
</div>


  <div data-aos="fade-left" className="project-left">
    <div className='p-left-image'>
    <img className="project-img" src="https://i.ibb.co/ZLFk69C/randompokemon.png"></img>
    </div>
    <div className ='p-left-content'>
      <h1 className="left-title">Random Pokemon</h1>
       <p className='languages-used'> | Redux | React | CSS | HTML | JavaScript | API | 
       </p>
       <p className='project-about-p'> I used the pokemon API to grab a random pokemon then fetched the returned URL to find it's name and type! I then displayed them for the user. </p>
      <div className='left-buttons'>
      <a className='left-links' href="https://github.com/CatRadin/React-Redux-App/tree/catherine-radin" 
        target="_blank">Github</a>
        <a className='left-links' href="https://random-pokemon.netlify.app/" 
        target="_blank">Demo</a>
      </div>
    </div>
  </div>

{/* <div className='divider'></div>
<div className='divider'></div>
<div className='divider'></div>
<div className='divider'></div> */}

<div data-aos="fade-right" className="project-right">
